// recursions of all the patch frequencies
int psys_recur(void *params, gsl_vector *f)
{
    //
double d = ((struct rparams *) params)->d;
double v = ((struct rparams *) params)->v;
double c = ((struct rparams *) params)->c;
double pinit = ((struct rparams *) params)->pinit;
double f_0 = ((struct rparams *) params)->f_0;
double f_1 = ((struct rparams *) params)->f_1;
double p = ((struct rparams *) params)->p;
double vh_1 = ((struct rparams *) params)->vh_1;
double vh_2 = ((struct rparams *) params)->vh_2;
double vd_0 = ((struct rparams *) params)->vd_0;
double vd_1 = ((struct rparams *) params)->vd_1;
double rdd = ((struct rparams *) params)->rdd;
double rhd = ((struct rparams *) params)->rhd;
double rhh = ((struct rparams *) params)->rhh;

    
    
    // 

double df0dt = 0;

double df1dt = 0;

for (int rtime=0;rtime<1e7;++rtime)
{

 df0dt=f_0 + 0.01*(-((1 - v/2.)*f_0*(2*(1 - d)*p + d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1))) + (1 - v)*f_1*(1 + (-2*(1 - d)*p - d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1))/2.));

df1dt=f_1 + 0.01*((1 - v/2.)*f_0*(2*(1 - d)*p + d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1)) - (f_1*(2*(1 - d)*p + d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1)))/2. + 2*(1 + c/2. - v/2.)*(1 - f_0 - f_1)*(1 + (-2*(1 - d)*p - d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1))/2.) - (1 - v)*f_1*(1 + (-2*(1 - d)*p - d*(2*p*f_0 + 2*p*(1 - f_0 - f_1) + 2*p*f_1))/2.));

if (fabs(df0dt-f_0)<1e-7 && fabs(df1dt-f_1)<1e-7)
{

f_0=bound(df0dt);

f_1=bound(df1dt);

break;
}
f_0=bound(df0dt);

f_1=bound(df1dt);

}
gsl_vector_set(f,0,df0dt);
gsl_vector_set(f,1,df1dt);
    
    return GSL_SUCCESS;
}

